mapper.select:
select bnum, category, name, content, price, discount, buycount, image1 from bedRoom
where category=#{category}
order by bnum desc

select bnum, category, name, content, price, discount, buycount, image1 from bedRoom
where category=#{category}
order by price asc

select bnum, category, name, content, price, discount, buycount, image1 from bedRoom
where category=#{category}
order by price desc

select bnum, category, name, content, price, discount, buycount, image1 from bedRoom
where category=#{category}
order by buycount desc

		select rownum, bnum, category, name, content, price, discount, buycount, image1
  		  from (select * from bedRoom order by buycount desc)
 		 where rownum between 1 and 3

		select rownum, bnum, category, name, content, price, discount, buycount, image1
  		  from (select * from bedRoom order by buycount desc)
 		 where rownum between 1 and 3
 		   and category = #{category}
