mapper.update:
update myhome
set goodCnt = goodCnt +1
where hnum=#{hnum}

update myhome
set title = #{title},content = #{content}

	,img=#{img}

where hnum = #{hnum}

update myhome
set readCnt = readCnt+1
where hnum = #{hnum}

mapper.select:
select hnum,id,title,img,content,author,to_char(writeday,'yyyy-MM-dd') as "writeday",readCnt,goodCnt
from myhome
where hnum=#{hnum}
